<span class="line"><span style="color:#F78C6C;">FROM</span><span style="color:#A6ACCD;"> employees </span><span style="color:#F78C6C;">AS</span><span style="color:#A6ACCD;"> a </span><span style="color:#F78C6C;">JOIN</span><span style="color:#A6ACCD;"> employees </span><span style="color:#F78C6C;">AS</span><span style="color:#A6ACCD;"> b</span></span>
